mentions.map:
function (doc) {
    if(doc.mentions){
        var i;
        for (i in doc.mentions){
            emit(doc.mentions[i],doc._id);
        }

    }
  }

tag.map:
function (doc) {
    if(doc.tags){
        var i;
        for (i in doc.tags){
            emit([doc.tags[i],parseInt(doc._id.substring(46))],doc._id);
        }

    }
  }

handles.map:
function (doc) {
    if(doc._id.length<=46){
        if(doc.handle){
            var json={
                id:doc._id,
                name:doc.name,
                location:doc.location
            };
            emit(doc.handle, json);
        }
    }
}
